jQuery(function () {
    jQuery('[data-toggle="popover"]').popover()
})

jQuery(function () {
    jQuery('[data-toggle="tooltip"]').tooltip()
})


jQuery(document).ready(function () {

    // tooltip show
    jQuery('.moduleslink a').tooltip({ trigger: 'manual' }).tooltip('show');

});


jQuery(document).ready(() => {


    jQuery('.nav-tabs').scrollingTabs({
        bootstrapVersion: 4,
        scrollToTabEdge: true,
        enableSwiping: true,
        disableScrollArrowsOnFullyScrolled: true,
        leftArrowContent: `
        <div class="scrtabs-tab-scroll-arrow scrtabs-tab-scroll-arrow-left">
          <button class="scrtabs-arrow-btn scrtabs-click-target" type="button">
            <i class="fa fa-chevron-left" aria-hidden="true"></i>
          </button>
        </div>`,
        rightArrowContent: `
        <div class="scrtabs-tab-scroll-arrow scrtabs-tab-scroll-arrow-right">
          <button class="scrtabs-arrow-btn scrtabs-click-target" type="button">
          <i class="fa fa-chevron-right" aria-hidden="true"></i>
          </button>
        </div>`
    }).on('ready.scrtabs', () => {
        jQuery('.nav-link').click(() => {
            setTimeout(() => {
                jQuery('.nav-tabs').scrollingTabs('scrollToActiveTab');
            }, 10);
        });
    });
});